class u{static calculateScore(t){const s={connection:.3,privacy:.3,network:.2,domain:.2};return Math.round(t.connection*s.connection+t.privacy*s.privacy+t.network*s.network+t.domain*s.domain)}static getStatus(t){return t<30?"SAFE":t<60?"WARNING":"DANGEROUS"}static analyzeDomain(t){const s=[];let n=0;try{const e=new URL(t),i=e.hostname;e.protocol!=="https:"&&(n+=30,s.push("Insecure connection (HTTP)")),i.split(".").length>4&&(n+=15,s.push("Deep subdomain structure")),[".xyz",".top",".buzz",".gq",".tk",".ml"].some(o=>i.endsWith(o))&&(n+=20,s.push("Suspicious Top-Level Domain (TLD)")),/^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$/.test(i)&&(n+=25,s.push("Accessed via IP address instead of domain"))}catch{n=100,s.push("Invalid URL")}return{score:Math.min(n,100),issues:s}}}export{u as R};
